x0, y0 = 5647, 1784
tiles = {}
solen_flow_lines = SOLEN_FLOW.strip().split("\n")
for i, line in enumerate(solen_flow_lines):
    for j, c in enumerate(line):
        if c not in (" ", "X"):
            tiles[(x0 + j, y0 + i)] = c


def execute_step():
    pos = Player.Position
    pos = (pos.X, pos.Y)
    if pos not in tiles:
        Misc.SendMessage("Out of bound!", 33)
        return False
    tile = tiles[pos]
    if tile == "@":
        # Reached the end
        return True
    if tile == "→":
        Player.Run("East")
    elif tile == "←":
        Player.Run("West")
    elif tile == "↑":
        Player.Run("North")
    elif tile == "↓":
        Player.Run("South")
    elif tile == "↗":
        Player.Run("Right")
    elif tile == "↙":
        Player.Run("Left")
    elif tile == "↖":
        Player.Run("Up")
    elif tile == "↘":
        Player.Run("Down")
    else:
        Misc.SendMessage("Unknown direction", 33)
        return False


################################################################################
# Quest Module
################################################################################


def get_player_pos() -> Tuple[int, int, int]:
    return (Player.Position.X, Player.Position.Y, Player.Position.Z)


def get_dist_to(x: int, y: int) -> int:
    x0, y0, _ = get_player_pos()
    dist = max(abs(x - x0), abs(y - y0))
    return dist


def is_in_ant_cave():
    if Player.Map not in (0, 1):
        return False
    x, y, _ = get_player_pos()
    if x < 5631 or x > 5938:
        return False
    if y < 1775 or y > 2036:
        return False
    if Player.Map != 1:
        return False
    return True


def wait_until_move(delay: int = 3000):
    pos = get_player_pos()
    Timer.Create("wait-move", 3000)
    while Timer.Check("wait-move"):
        if get_player_pos() != pos:
            return True
        Misc.Pause(100)
    return False


def find_enemies(range_max: int = 6) -> List["Mobile"]:  # type: ignore
    filter = Mobiles.Filter()
    filter.Enabled = True
    filter.Notorieties = CList[Byte](b"\x03\x04\x05\x06")
    filter.Warmode = True
    filter.RangeMax = range_max
    return Mobiles.ApplyFilter(filter)


def flow_in_solen_cave(tolerance: int = 1) -> bool:
    """Follow the path in the Solen cave."""

    ask_to_run(running=True)
    Misc.SendMessage("Following the path.", 1153)

    while True:
        # Check if we are in the cave
        if not is_in_ant_cave():
            Misc.SendMessage("You are not in the Solen cave.", 33)
            return False

        # Enter the hole if there is one
        if get_dist_to(5661, 1955) <= 10 or get_dist_to(5901, 1878) <= 10:  # Tunnels
            hole = Items.FindByID(0x0495, 1, -1, 3)
            if hole is not None:
                Items.UseItem(hole.Serial)
                wait_until_move()

        # Check if we have reached the destination
        if get_dist_to(5868, 1795) <= tolerance:  # Minoc
            return True
        if get_dist_to(5742, 1820) <= tolerance:  # Yew
            return True
        if get_dist_to(5739, 1941) <= tolerance:  # Trinsic
            return True
        if get_dist_to(5912, 1944) <= tolerance:  # Desert
            return True
        if get_dist_to(5671, 1866) <= tolerance:  # Secret
            return True

        # Handle potential enemies on the way
        enemies = find_enemies(range_max=ENEMY_RANGE_MAX)
        if ENEMY_CHECK and enemies:
            return False

        # Check for gump response
        if parse_response(delay=100):
            Misc.SendMessage("Running stopped.", 1153)
            return False
        
        # Take a step
        execute_step()


GUMP_WT = """<CENTER><BASEFONT COLOR="#FFFFFF">{text}</BASEFONT></CENTER>"""
SHORTCUT_GUMP_ID = hash("NaturalistHelperGump") & 0xFFFFFFFF


def ask_to_run(running: bool = False):
    """
    A minimized gump with a button to run.
    """
    Gumps.CloseGump(SHORTCUT_GUMP_ID)
    gd = Gumps.CreateGump(movable=True)
    Gumps.AddPage(gd, 0)
    Gumps.AddBackground(gd, 0, 0, 146, 65, 30546)
    Gumps.AddAlphaRegion(gd, 0, 0, 146, 65)
    Gumps.AddHtml(gd, 10, 5, 126, 18, GUMP_WT.format(text="Naturalist Helper"), False, False)
    if running:
        Gumps.AddButton(gd, 10, 30, 40297, 40298, 1, 1, 0)
        Gumps.AddHtml(gd, 10, 33, 126, 18, GUMP_WT.format(text="Stop Running"), False, False)
    else:
        Gumps.AddButton(gd, 10, 30, 40021, 40031, 1, 1, 0)
        Gumps.AddHtml(gd, 10, 33, 126, 18, GUMP_WT.format(text="To the Nest"), False, False)
    Gumps.SendGump(SHORTCUT_GUMP_ID, Player.Serial, 100, 100, gd.gumpDefinition, gd.gumpStrings)


def parse_response(delay: int = 500) -> bool:
    if not Gumps.WaitForGump(SHORTCUT_GUMP_ID, delay):
        return False
    gd = Gumps.GetGumpData(SHORTCUT_GUMP_ID)
    if gd is None:
        return False
    return gd.buttonid == 1


if __name__ == "__main__":
    while Player.Connected:
        # Pop up the gump and wait for response
        ask_to_run()
        while not parse_response():
            continue

        flow_in_solen_cave()
